import React, { useEffect, useState } from 'react'
import axios from 'axios';
import { Modal, Button } from 'react-bootstrap';

import './css/search.css'
function SearchMaterial() {

    let[info, setInfo] = useState([]);
    const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
    async function getAllMaterialList(){
        await axios.get(`http://localhost:3000/material`)
        .then(res => {
            setInfo(res.data)
        })
    }
    useEffect(()=>{
        getAllMaterialList()
        console.log(info)
    },[])

    const addMaterialPopup = () =>{
        setShow(true)
        return(
            <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
        )
    }
  return (
    <div>
        <table className="table">
            <thead>
                <tr>
                <th scope="col">ID</th>
                <th scope="col">Name</th>
                <th scope="col">Quantity</th>
                <th scope="col">Price</th>
                <th scope="col">Action</th>
                </tr>
            </thead>
            <tbody>

                    {
                        info.map(data=>{
                            return (
                                <tr>
                            <th scope="row">{data.id}</th>
                            <td>{data.name}</td>
                            <td colSpan="1">
                                <div className="row">
                                    <div className="col-3">{data.quantity}</div>
                                    <div className="col-4">
                                        <button className='btn btn-outline-primary' onClick={()=>addMaterialPopup}>+</button>
                                        </div>
                                </div></td>
                            <td>{data.price}</td>
                            <td>üñãÔ∏è</td>
                            </tr>
                            )

                        })
                    }


            </tbody>
    </table>
    </div>
  )
}

export default SearchMaterial
