import React, { useEffect, useState } from 'react'
import axios from 'axios';
import './sell.css'
function Sell() {
    const[ids, setIds] = useState([])
    const[material, setMaterial] = useState([])
    function getAllAccountNumber(){
        axios.get(`http://localhost:3000/add_account`)
        .then(res => {
            setIds(res.data)
        })
    }

    function handleChange(event) {
        event.preventDefault();
        console.log(event.target.value);
        getMaterialName(event.target.value)
      }

    function getMaterialName(name){
        // getAllMaterial
        let url = `http://localhost:3000/getAllMaterial/:${name}`
        axios.get(url)
        .then(res => {
            console.log(res.data)
            setMaterial(res.data)
        })
    }
    useEffect(() => {
        getAllAccountNumber()
    }, [])

  return (
    <div>
        <div className="col-12">
            <div className="row">
                <div className="col-6">
                    <h5>खात नंबर</h5>
                </div>
                <div className="col-6">
                    <select name="" id="">
                        <option value="" >खाता नंबर निवडा</option>
                        {ids.map((data)=>{
                            return <option value="">{data.id}</option>
                        })}
                    </select>
                </div>
            </div>
            <div className="row">
                <div className="col-6">
                    <h5>मटेरियल</h5>
                </div>
                <div className="col-6">
                    <input type="text" name="" id="" onChange={e => getMaterialName(e.target.value)} value={e=>e.target.value}/>
                </div>
            </div>
            <div className="row">
                <div className="col-6">
                    <h5>उधार/रोख</h5>
                </div>
                <div className="col-6">
                    <input type="text" name="" id="" />
                </div>
            </div>
            <div className="row">
                <div className="col-6">
                    <h5>किंमत</h5>
                </div>
                <div className="col-6">
                    <input type="number" name="" id="" />
                </div>
            </div>
            <div className="row">
                <div className="col-6">
                    <button className='btn btn-success'>जमा</button>
                </div>
                <div className="col-6">
                    <button className='btn btn-danger'>cancel</button>
                </div>
            </div>
        </div>
    </div>
  )
}

export default Sell
