import React, { useEffect, useState } from 'react'
import axios from 'axios';
import './css/search.css'
function SearchMaterial() {

    let[info, setInfo] = useState([]);

    async function getAllMaterialList(){
        await axios.get(`http://localhost:3000/material`)
        .then(res => {
            setInfo(res.data)
        })
    }
    useEffect(()=>{
        getAllMaterialList()
        console.log(info)
    },[])

    function addMaterialPopup(){
        return(
                <div className="modal">
                    <h1>Hello</h1>
                </div>
        )
    }
  return (
    <div>
        <table classNameName="table">
            <thead>
                <tr>
                <th scope="col">ID</th>
                <th scope="col">Name</th>
                <th scope="col">Quantity</th>
                <th scope="col">Price</th>
                <th scope="col">Action</th>
                </tr>
            </thead>
            <tbody>

                    {
                        info.map(data=>{
                            return (
                                <tr>
                            <th scope="row">{data.id}</th>
                            <td>{data.name}</td>
                            <td colSpan="1">
                                <div classNameName="row">
                                    <div classNameName="col-3">{data.quantity}</div>
                                    <div classNameName="col-4">
                                        <button classNameName='btn btn-outline-primary' onClick={addMaterialPopup}>+</button>
                                        </div>
                                </div></td>
                            <td>{data.price}</td>
                            <td>🖋️</td>
                            </tr>
                            )

                        })
                    }


            </tbody>
    </table>
    </div>
  )
}

export default SearchMaterial
