import React, { useRef } from 'react'
import axios from 'axios';
import { Toast } from 'react-bootstrap';
function AddUser() {

    let name = useRef();
    let amount = useRef();
    const [showA, setShowA] = useState(true);
    const [showB, setShowB] = useState(true);

    const toggleShowA = () => setShowA(!showA);
    const toggleShowB = () => setShowB(!showB);
    const addEntry = () =>{
        let body={
            "name":name.current.value,
            "amount": Number(amount.current.value)
        }
        console.log(body)
        axios.post(`http://localhost:3000/add_account`,body).then(res=>{
        if(res.status == 200){
            setShowA(!showA)
            return (
                <Toast show={showA} onClose={toggleShowA}>
          <Toast.Header>
            <img
              src="holder.js/20x20?text=%20"
              className="rounded me-2"
              alt=""
            />
            <strong className="me-auto">Bootstrap</strong>
            <small>11 mins ago</small>
          </Toast.Header>
          <Toast.Body>Woohoo, you're reading this text in a Toast!</Toast.Body>
        </Toast>
            )

        }}).catch(err=>console.log("err",err));
    }
  return (
    <div>
        <div className="col-12">
            <div className="row">
                <div className="col-6">
                    <h5>नाव </h5>
                </div>
                <div className="col-6">
                    <input type="text" name="" id="" ref={name}/>
                </div>
            </div>
            <div className="row">
                <div className="col-6">
                    <h5>रोख ऍडव्हान्स </h5>
                </div>
                <div className="col-6">
                    <input type="number" name="" id="" ref={amount}/>
                </div>
            </div>
            <div className="row mt-3">
                <div className="col-6 d-grid d-md-flex justify-content-md-end">
                    <button className='btn btn-success jama' onClick={addEntry}>एन्ट्री करा</button>
                </div>
                <div className="col-6 d-grid d-md-flex justify-content-md-end">
                    <button className='btn btn-danger'>cancel</button>
                </div>
            </div>
        </div>
    </div>
  )
}

export default AddUser
