import React, { useEffect, useRef, useState } from 'react'
import axios from 'axios';
import { ReactSearchAutocomplete } from 'react-search-autocomplete'
import './sell.css'

function Sell() {
    const[ids, setIds] = useState([])
    const[material, setMaterial] = useState([])
    let m_name, m_num;
    let type;
    let amount;
    let[acNumber, setacNumber] = useState();
    const inputEl = useRef(null)

    function getAllAccountNumber(){
        axios.get(`http://localhost:3000/add_account`)
        .then(res => {
            setIds(res.data)
        })
    }

    function handleSelectChange(event) {
        type = event.target.value;
    }
    function handleAccountNumber(event){
        setacNumber(event.target.value)
    }
    function getMaterialName(name){
        // getAllMaterial
        let url = `http://localhost:3000/getAllMaterial/${name}`
        axios.get(url)
        .then(res => {
            setMaterial(res.data)
        })
    }
    const onButtonClick = () => {
        let price = inputEl.current;
        amount = price.value;
        console.log(amount,acNumber,type,m_name)
        let body={
                "material_id":m_name,
                "quantity":20,
                "id":4,
                "pType": "debit_from_account",
                "amount":10
        }
      };

    useEffect(() => {
        getAllAccountNumber()
    }, [])

  return (
    <div>
        <div className="col-12">
            <div className="row">
                <div className="col-6">
                    <h5>खात नंबर</h5>
                </div>
                <div className="col-6">
                    <select name="" id="" onChange={handleAccountNumber}>
                        <option value="" >खाता नंबर निवडा</option>
                        {ids.map((data)=>{
                            return <option value={data.id}>{data.id}</option>
                        })}
                    </select>
                </div>
            </div>
            <div className="row">
                <div className="col-6">
                    <h5>मटेरियल</h5>
                </div>
                <div className="col-6">
                    <input type="text" name="" id="" onChange={e => getMaterialName(e.target.value)}/>
                    <div className="autoComplete" >
                        {material.map((data) => {return <div>
                            <p onClick={e => m_name = e.target.innerText, m_num =data.id } value={data.id}>{data.name}</p>
                        </div>})}
                    </div>
                </div>
            </div>
            <div className="row">
                <div className="col-6">
                    <h5>उधार/रोख</h5>
                </div>
                <div className="col-6">
                    <select name="" id="" onChange={handleSelectChange}>
                        <option value="debit_from_account">खात्यातून वजा करा</option>
                        <option value="credit_on_account">खात्यावर मांडा</option>
                        <option value="hard_cash">रोख रक्कम</option>
                    </select>
                </div>
            </div>
            <div className="row">
                <div className="col-6">
                    <h5>किंमत</h5>
                </div>
                <div className="col-6">
                    <input type="number" name="" id="" ref={inputEl}/>
                </div>
            </div>
            <div className="row">
                <div className="col-6">
                    <button className='btn btn-success' onClick={onButtonClick}>जमा</button>
                </div>
                <div className="col-6">
                    <button className='btn btn-danger'>cancel</button>
                </div>
            </div>
        </div>
    </div>
  )
}

export default Sell
